$.tbFavAnime=function(options){if(options.images!==undefined){var favcounter=1;var favimgs=options.images;var interval;var path_to_img='';var defIco=$('link[rel=icon]').attr('href');var animeType=1
    if(options.type!==undefined&&(options.type==0||options.type==2)){animeType=options.type}
    if(options.imgPath!==undefined&&options.imgPath!=''){path_to_img=options.imgPath}
    function startAnime(){interval=setInterval(function(){if(favcounter==favimgs.length-1){favcounter=0}else{favcounter++}
        console.log(favcounter);$('link[rel=icon]').attr('href',path_to_img+favimgs[favcounter])},(options.interval!==undefined)?options.interval:500)}
    function stopAnime(){if(interval!==undefined){clearInterval(interval)}
        $('link[rel=icon]').attr('href',defIco)}
    if(animeType==0){startAnime()}else if(animeType==2){$(window).focus(function(){startAnime()});$(window).blur(function(){stopAnime()})}else{$(window).blur(function(){startAnime()});$(window).focus(function(){stopAnime()})}}}